        private void btnEncode_Click(object sender, EventArgs e)
        {
            Encode();
        }

        private void Encode()
        {
            byte[] bytes = Encoding.Unicode.GetBytes(textBoxPsCmd.Text);
            string base64 = Convert.ToBase64String(bytes);
            textBoxPsEncoded.Text = base64;
        }

        private void buttonDecode_Click(object sender, EventArgs e)
        {
            Decode();
        }

        private void Decode()
        {
            try
            {
                string s = textBoxPsEncoded.Text;
                string plain = Encoding.Unicode.GetString(Convert.FromBase64String(s));
                textBoxPsCmd.Text = plain;
            }
            catch (Exception ex)
            {
                MessageBox.Show(ex.Message);
            }
        }

        private void buttonExecute_Click(object sender, EventArgs e)
        {
            textBoxResponse.Text = "Running.....";
            Encode();
            Execute();
        }

        private async void Execute()
        {
            JsonCmd json = new JsonCmd
            {
                cmd = comboBoxCmd.SelectedItem.ToString(),
                cmdType = comboBoxCmdType.SelectedItem.ToString(),
                cmdText = textBoxPsEncoded.Text
            };
            string responseString =string.Empty; 
            try
            {
                string jsonCmd = JsonConvert.SerializeObject(json);
                Task<string> response = new AsyncCall().WebApiCall(
                    textBoxEndPoint.Text,
                    GetPostType.POST,
                    textBoxAction.Text,
                    jsonCmd,
                    textBoxAccessKey.Text,
                    textBoxSecretKey.Text
                    );
                string temp = await response;
                JToken jt = JToken.Parse(temp);
                responseString = jt.ToString(Newtonsoft.Json.Formatting.Indented);
            }
            catch (Exception ex)
            {
                responseString = ex.Message; 
            }
            responseString = Regex.Replace(responseString, @"\\r\\n?|\\n", Environment.NewLine);
            textBoxResponse.Text = Regex.Replace(responseString, @"\\\\", @"\");
        }

        private void cmd_SelectedIndexChanged(object sender, EventArgs e)
        {
            if (comboBoxCmd.SelectedItem.ToString().ToUpper() != "POWERSHELL")
            {
                comboBoxCmdType.Items.Clear();
                comboBoxCmdType.Items.Add("START");
                comboBoxCmdType.Items.Add("STOP");
                comboBoxCmdType.SelectedItem = "STOP";
                textBoxPsCmd.Text = "";
            }
            else
            {
                comboBoxCmdType.Items.Clear();
                comboBoxCmdType.Items.Add("OUT-STRING");
                comboBoxCmdType.Items.Add("OBJECT");
                comboBoxCmdType.SelectedItem = "OUT-STRING";
                textBoxPsCmd.Text = @"dir 'c:\Program Files\NBP\SQLServerLogCollector\log'"; 
            }
        }

